<?php if(empty($tours)) echo '<p>No tours</p>'; else { echo '<h2>Admin Tours</h2><a href="/index.php/admin/tours/create">Create tour</a><table><tr><th>ID</th><th>Name</th><th>Type</th><th>Price</th><th>Action</th></tr>'; foreach($tours as $t){ echo '<tr><td>'.$t['tour_id'].'</td><td>'.htmlspecialchars($t['tour_name']).'</td><td>'.$t['tour_type'].'</td><td>'.number_format($t['price']).'</td><td><a href="/index.php/admin/tours/edit?id='.$t['tour_id'].'">Edit</a></td></tr>'; } echo '</table>'; }