<h2>Public Tours</h2><?php if(empty($tours)){ echo '<p>No tours.</p>'; } else { echo '<table><tr><th>ID</th><th>Name</th><th>Type</th><th>Price</th><th>Action</th></tr>'; foreach($tours as $t){ echo '<tr><td>'.$t['tour_id'].'</td><td>'.htmlspecialchars($t['tour_name']).'</td><td>'.$t['tour_type'].'</td><td>'.number_format($t['price']).'</td><td><a href="/index.php/tours/show?id='.$t['tour_id'].'">View</a></td></tr>'; } echo '</table>'; }